import axios from 'axios'
import React from 'react'
import { useHistory } from 'react-router'
import "./cards.css"

const Posts = ({ posts, getPosts }) => {

    const history = useHistory();

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:8000/posts/delete/${id}`, {
                headers: {
                    "x-auth-token": localStorage.getItem('x-auth-token')
                }
            })
            getPosts();
        } catch (error) {
            console.log(error)

        }

    }
    const handleUpdate = (id) => {
        history.push(`/posts/details/${id}`); // Navigate to post details page with the specific post ID
    }

    return (
        <div>
            {posts.map((post, index) => (
                <div key={index} className="card">
                    <div className="container">
                        <div className='headings'>
                            <p>Id</p>
                            <p>Image</p>
                            <p>Name</p>
                            <p>Email</p>
                            <p>Mobile </p>
                            <p>Designation</p>
                            <p>Gender</p>
                            <p>Course</p>
                            <p>Date</p>
                            <p>Action</p>

                        </div>
                        <div className='posts'>
                            <p>{post.index}</p>
                            <p><b>{post.title}</b></p>
                            <p>{post.body}</p>
                            <p>{post.name}</p> {/* Add these lines to display other details */}
                            <p>{post.email}</p>
                            <p>{post.mobile}</p>
                            <p>{post.designation}</p>
                            <p>{post.gender}</p>
                            <p>{post.course}</p>
                            <p>{post.createdAt}</p>
                        </div>
                        <div className="button">
                            <button onClick={() => handleUpdate(post._id)} className="btn btn-warning" style={{ marginRight: "5px" }}>Edit</button>
                            <button onClick={() => handleDelete(post._id)} className="btn btn-info ">Delete</button>
                        </div>
                    </div>

                </div>
            ))}

        </div>
    )
}

export default Posts
